
import {
    getAjustes,
    getAsociados,
    getBarrios,
    getClientes,
    getCondicionesIva,
    getDocumentaciones,
    getEntregasRopa,
    getEstadosCiviles,
    getEstadosDocumentacion,
    getFeriados, 
    getLineasDocumentacion,
    getLineasEntregaRopa,
    getLineasServicios,
    getLocalidades,
    getModalidades,
    getMotivos,
    getObjetivos,
    getPrendas,
    getProvincias,
    getRecibos,
    getServicios,
    getTalles,
    getTiposAjustes,
    getTiposDocumentacion,
    getTiposMotivos,
    getTiposPrendas,
    getTiposTelefonos,
    getValores,
  } from "./zthunks"; 
  
  export const loadInitialResources = async (dispatch) => {
    const thunks = [
      getAjustes,
      getAsociados,
      getBarrios,
      getClientes,
      getCondicionesIva,
      getDocumentaciones,
      getEntregasRopa,
      getEstadosCiviles,
      getEstadosDocumentacion,
      getFeriados,
      getLineasDocumentacion,
      getLineasEntregaRopa,
      getLineasServicios,
      getLocalidades,
      getModalidades,
      getMotivos,
      getObjetivos,
      getPrendas,
      getProvincias,
      getRecibos,
      getServicios,
      getTalles,
      getTiposAjustes,
      getTiposDocumentacion,
      getTiposMotivos,
      getTiposPrendas,
      getTiposTelefonos,
      getValores,
    ];
  
    await Promise.all(thunks.map((thunk) => dispatch(thunk())));
  };
  